import { useContext, useEffect, useState } from "react";
import { ServerEventsContext } from "./ServerEventsProvider";

export interface ProcessingResult<T> {
  data: T | undefined;
}

export const useServerEvents = <T extends object>(): ProcessingResult<T> => {
  //const [subscriberID, setSubscriberID] = useState<number>(0);
  const [eventData, setEventData] = useState<T>();

  //const formatEvent = `@sse-lib/${eventType}`

  const eventSource = useContext(ServerEventsContext);
  console.log(eventSource);
  //console.log('eventSource -> useContext: ', eventSource)

  if (!eventSource) {
    throw new Error(`To use "useServerEvents()" wrap your application in provider:
        <ServerEventsProvider>
          <YourApp />
        </ServerEventsProvider>
    `);
  }

  eventSource.onmessage = (event) => {
    console.log("eventSource.onmessage: ", event);
    const data = JSON.parse(event.data);
    setEventData(data);
  };

  return {
    data: eventData,
  };
};
